import React from 'react';
import './Article.css';
class Article extends React.Component {
    constructor(props){
        this.state={
            loadingText:'Loading',
            text:'', 
            x: 0, 
            y: 0,
            display:'none'
        }
    }
    _onMouseUp(e) {
        this.setState({ x: e.screenX, y: e.screenY });
      }
    translate=(data)=>{
        fetch('http://localhost:3000/translate',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                word:data
            })
        })
        .then(response => response.json())
        .then(translation => {
          if(translation){
            this.setState({text:translation})
            this.setState({loadingText:''})
          }
        })
    }

    getSelectionText=()=> {
        if (window.getSelection) {
            this.translate(window.getSelection().toString());
            this.setState({display:'block'});
            if(window.getSelection().toString()==='')
            {this.setState({display:'none'});}
        } else if (document.selection && document.selection.type !== "Control"){
            this.translate(document.selection.createRange().text);
        }else{
            this.setState({text:''});
        }
    }
    render(){
    const{onRouteChange,reading}=this.props;
    const{x,y,text,display,loadingText}=this.state;
    return(
        <div className="center"  onClick={this.getSelectionText} onMouseUp={this._onMouseUp.bind(this)}>
            <p className="br white fw6 f4 lh-copy measure ">
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
            </p>
            <br/>
            <div id="infoDiv" className="tooltipDiv" style={{display:display,left:x,top:y}}>{loadingText}{text}</div>
        </div>  
    );
}
}

export default Article;
