import React from 'react';
import './Article.css';
import * as $ from 'jquery';
class Article extends React.Component {
    constructor(props){
        super(props);
        this.state={
            text:'init',
            x: 0, 
            y: 0 
        }
    }
    _onMouseMove(e) {
        this.setState({ x: e.screenX, y: e.screenY });
      }
    popup=(event)=> {
        var selectedText = this.getSelectedText();
        if (selectedText.replace(/\s/g, '').length) {
        if (selectedText&&selectedText!=="") {
          $('#infoDiv').css('display', 'block');
          $('#infoDiv').css('position', 'absolute');
          $('#infoDiv').css('left', event.pageX );
          $('#infoDiv').css('top', event.pageY );
        } else {
          $('#infoDiv').css('display', 'none');
          };
        }
        else{  $('#infoDiv').css('display', 'none');}
      }
    translate=(data)=>{
        fetch('http://localhost:3000/translate',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                word:data
            })
        })
        .then(response => response.json())
        .then(translation => {
          if(translation){
            this.setState({text:translation})
          }
        })
    }
    getSelectedText=()=> {
        if (window.getSelection) {
            this.setState({text:this.translate(window.getSelection().toString())});
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({text:this.translate(document.selection.createRange().text)});
        }else{
            this.setState({text:''});
        }
        return this.state.text;
    }
    render(){
    const{onRouteChange,reading,x,y}=this.props;
    return(
        <div className="center" onMouseUp={this.getSelectedText} onMouseMove={this._onMouseMove.bind(this)}>
            <p className="br white fw6 f4 lh-copy measure ">
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
                {this.state.text}
            </p>
        </div>  
    );
}
}

export default Article;
