import React from 'react';
import './Article.css';
class Article extends React.Component {
    constructor(props){
        super(props);
        this.state={
            text:'init', 
            x: 0, 
            y: 0,
            style:{
                display:'',
            }
        }
    }
    _onMouseMove(e) {
        this.setState({ x: e.screenX, y: e.screenY });
        this.setState({ style:{display:'block',position:'absolute',left: e.screenX, top: e.screenY }});
      }
    show=()=>{
        this.setState({style:{display:'block'}});
        console.log(this.state.style)
    }
    translate=(data)=>{
        fetch('http://localhost:3000/translate',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                word:data
            })
        })
        .then(response => response.json())
        .then(translation => {
          if(translation){
            this.setState({text:translation})
          }
        })
    }
    getSelectionText=()=> {
        if (window.getSelection) {
            this.setState({text:this.translate(window.getSelection().toString())});
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({text:this.translate(document.selection.createRange().text)});
        }else{
            this.setState({text:''});
        }
    }
    render(){
    const{onRouteChange,reading}=this.props;
    const{x,y,style}=this.state;
    return(
        <div className="center"  onMouseMove={this._onMouseMove.bind(this)}onClick={this.show}>
            <p className="br white fw6 f4 lh-copy measure ">
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
                {this.state.text}
            </p>
            <br/>
            <p style={{left:x,top:y,position:absolute}}>hahaha</p>
            <h1>Mouse coordinates: { x } { y }</h1>
            <div style={{style}}id="infoDiv" class="tooltipDiv">hahahah</div>
        </div>  
    );
}
}

export default Article;
