import React, { Component } from 'react';
import './App.css';
import Sky from 'react-sky';
// import Clarifai from 'clarifai';
import banana from './Components/Pics/banana.png';
import duck from './Components/Pics/duck.png';
import star from './Components/Pics/star.png';
import mouse from './Components/Pics/mouse.png';
import Navigation from './Components/Navigation/Navigation';
import ArticleBox from './Components/ArticleBox/ArticleBox';
import SignIn from './Components/SignIn/SignIn';
import Register from './Components/Register/Register';
import Article from './Components/Article/Article';
import Profile from './Components/Profile/Profile';

const skyParam={
    0: banana,
    1: duck,
    2: star,
    3: mouse,
};

// const app = new Clarifai.App({
//   apiKey: 'ee011192827e4a179a9e9c4e48c09e71'
// });
const initialState = {
  input: '',
  route:'signin',
  isSignedIn:false,
  reading:'',
  user:{
    id:'',
    name:'',
    email:'',
    password:'',
    entries:0,
    joined:''
  }
}
class App extends Component {
  constructor(){
    super();
    this.state=initialState;
  }
  loadUser=(data)=>{
    this.setState({user:{
      id:data.id,
      name:data.name,
      email:data.email,
      entries:data.entries,
      joined:data.joined
    }})
  }
  componentDidMount(){
    fetch('http://localhost:3000')
      .then(response => response.json())
      .then(console.log)
  }
  // calculateFaceLocation = (data) => {
  //   const clarifaiFace=data.outputs[0].data.regions[0].region_info.bounding_box;
  //   const image = document.getElementById('inputimage');
  //   const width = Number(image.width);
  //   const height = Number(image.height);
  //   return{
  //     leftCol: clarifaiFace.left_col * width,
  //     topRow: clarifaiFace.top_row * height,
  //     rightCol: width - (clarifaiFace.right_col * width),
  //     bottomRow: height - (clarifaiFace.bottom_row * height)
  //   }
  // }
  // disPlayBox = (boxCor) => {
  //   this.setState({box:boxCor});
  // }
  onInputChange = (event) => {
    this.setState({input:event.target.value});
  }
  onButtonSubmit = () => {
    // this.setState({imgUrl:this.state.input});
    this.setState({reading:this.state.input})
    //   app.models
    //   .predict(
    //     Clarifai.FACE_DETECT_MODEL,
    //     this.state.input)
      // .then(response => 
      //   {
      //     if(response){
            fetch('http://localhost:3000/image',{ 
              method:'put',
              headers:{'Content-Type':'application/json'},
              body:JSON.stringify({
              id:this.state.user.id,})
            })
            .then(response=>response.json())
            .then(count=>{
              this.setState(Object.assign(this.state.user,{
                entries:count})
              )
            })
          // }
          // this.disPlayBox(this.calculateFaceLocation(response))
        // }
        // )
      .catch(err => console.log(err));
  }
  onRouteChange = (route) => {
    if(route==='signout'){
      this.setState(initialState)
    }else if(route==='home'){
      this.setState({isSignedIn:true},
      this.setState({reading:''})
        )
    }
    this.setState({route:route});
  }
  render(){
    const{route,isSignedIn,reading} = this.state;
    if(route==='profile'){
      return(        <Profile id={this.state.user.id}/>
        )
    }
    if(reading===''){
    return (
      <div className="App">
        <Sky
          images={skyParam}
          how={150} /* Pass the number of images Sky will render chosing randomly */
          time={20} /* time of animation */
          size={'100px'} /* size of the rendered images */
          background={'palettedvioletred'} /* color of background */
        />
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
        { route === 'home'
          ?
            <div>
              <ArticleBox  
                onInputChange={this.onInputChange}
                onButtonSubmit={this.onButtonSubmit}
              />
              {/* <Rank name={this.state.user.name} entries={this.state.user.entries}/> */}
            </div>
          :(
            route ==='signin'
            ?<SignIn onRouteChange={this.onRouteChange}loadUser={this.loadUser}/>
            :<Register onRouteChange={this.onRouteChange} loadUser={this.loadUser}/>
            )
        }
      </div>
     );}
     else{
       return(
          <div>
            <Article reading={reading} onRouteChange={this.onRouteChange} id={this.state.user.id}/>
          </div>
         )
     }
  }
}

export default App;
