import React from 'react';
import './Article.css';
class Article extends React.Component {
    constructor(props){
        super(props);
        this.state={
            word:'',
            text:'init',
        }
    }
    translate=()=>{
        fetch('http://localhost:3000/translate',{
            method:'get',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                word:this.state.word
            })
        })
        .then(response => response.json())
        .then(translation => {
          if(translation){
            this.setState({text:translation})
          }
        })
    }
    getSelectionText=()=> {
        if (window.getSelection) {
            this.setState({word:window.getSelection().toString()});
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({word:document.selection.createRange().text});
        }else{
            this.setState({word:''});
        }
    }
    render(){
    const{onRouteChange,reading}=this.props;
    return(
        <div className="center" onMouseUp={this.getSelectionText}>
            <p className="br white fw6 f4 lh-copy measure ">
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
                {this.state.text}
            </p>
        </div>  
    );
}
}

export default Article;
