import React from 'react';
import './Article.css';
class Article extends React.Component {
    constructor(props){
        super(props);
        this.state={
            text:'init',
        }
    }
    getSelectionText=()=> {
        if (window.getSelection) {
            this.setState({text:window.getSelection().toString()});
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({text:document.selection.createRange().text});
        }else{
            this.setState({text:'no'});
        }
    }
    render(){
    const{onRouteChange,reading}=this.props;
    return(
        <div className="center">
            <p className="br white fw6 f4 lh-copy measure " onMouseUp={this.getSelectionText}>
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
                {this.state.text}
            </p>
        </div>  
    );
}
}

export default Article;
