import React from 'react';
import './Article.css';
class Article extends React.Component {
    constructor(props){
        super(props);
        this.state={
            text:'init', 
            x: 0, 
            y: 0,
            display:'block'
        }
    }
    _onMouseMove(e) {
        this.setState({ x: e.screenX, y: e.screenY });
      }
    show=()=>{
        if(this.state.display==='none'){
            this.setState({display:'block'})
        }
        else{
            this.setState({display:'none'})
        }
    }
    translate=(data)=>{
        fetch('http://localhost:3000/translate',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                word:data
            })
        })
        .then(response => response.json())
        .then(translation => {
          if(translation){
            this.setState({text:translation})
          }
        })
    }
    getSelectionText=()=> {
        if (window.getSelection) {
            this.setState({text:this.translate(window.getSelection().toString())});
            this.show();
            console.log(this.state.display);
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({text:this.translate(document.selection.createRange().text)});
        }else{
            this.setState({text:''});
            this.show();
        }
    }
    test=()=> {
        if (window.getSelection) {
            this.setState({text:window.getSelection().toString()});
            this.setState({display:'block'});
            if(window.getSelection().toString()==='')
            {this.setState({display:'none'});}
        } else if (document.selection && document.selection.type !== "Control"){
            this.setState({text:document.selection.createRange().text});
        }else{
            this.setState({text:''});
        }
    }
    render(){
    const{onRouteChange,reading}=this.props;
    const{x,y,text,display}=this.state;
    return(
        <div className="center"  onClick={this.test} onMouseUp={this._onMouseMove.bind(this)}>
            <p className="br white fw6 f4 lh-copy measure ">
                {reading}
                <br/>
                <button 
                    className='w-30 grow f4 link ph3 pv2 dib white bg-light-red' 
                    onClick={()=>onRouteChange('home')
                }
                    >Back</button>
                {text}
            </p>
            <br/>
            <h1>Mouse coordinates: { x } { y }</h1>
            <div id="infoDiv" className="tooltipDiv" style={{display:display,left:x,top:y}}>{text}</div>
        </div>  
    );
}
}

export default Article;
