import React, { Component } from 'react';
import './App.css';
import Sky from 'react-sky';
import Clarifai from 'clarifai';
import banana from './Components/Pics/banana.png';
import duck from './Components/Pics/duck.png';
import star from './Components/Pics/star.png';
import mouse from './Components/Pics/mouse.png';
import Navigation from './Components/Navigation/Navigation';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Rank from './Components/Rank/Rank';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';


const skyParam={
    /* FORMAT AS FOLLOWS */
    0: banana,
    1: duck,
    2: star,
    3: mouse,
};
const app = new Clarifai.App({
  apiKey: 'ee011192827e4a179a9e9c4e48c09e71'
});

class App extends Component {
  constructor(){
    super();
    this.state={
      input:'',
      imgUrl:'',
    }
  }


  onInputChange = (event) => {
    this.setState({input:event.target.value});
  }

  onButtonSubmit = () => {
    this.setState({imgUrl:this.state.input});
    console.log('click');
      app.models
      .predict(
        Clarifai.FACE_DETECT_MODEL,
        "https://samples.clarifai.com/face-det.jpg")
      .then(
        function(response) {
          console.log(response);
        },
        function(err) {
          // there was an error
        }
      );
  }

  render(){
    const {input,imgUrl}=this.state;
    return (
      <div className="App">
        <Sky
          images={skyParam}
          how={150} /* Pass the number of images Sky will render chosing randomly */
          time={10} /* time of animation */
          size={'100px'} /* size of the rendered images */
          background={'palettedvioletred'} /* color of background */
        />
        <Navigation />
        <Logo />
        <ImageLinkForm  
          onInputChange={this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        />
        <Rank />
        <FaceRecognition />
      </div>
     );
  }
}
export default App;
